import React, { ReactDOM, Fragment, Component } from 'react';
import Header from './layout/Header';
import Footer from './layout/Footer';
import MainMenu from './layout/MainMenu';
import './assets/css/command.post.css';

class CommandPost extends Component {

  state = {
    darkMode: false,
    userLoggedIn: false
  }

  switchThemeHandler = () => {
    const doesDarkMode = this.state.darkMode;
    this.setState({ darkMode: !doesDarkMode });
    ReactDOM.render(<link rel="stylesheet" href={'./assets/css/commandpost.dark.css'} type="text/css"></link>, document.head);
  }

  render() {
    return (
      <Fragment>
        <Header />
        <div className="main-wrapper">
          <MainMenu
            click={() => this.switchThemeHandler()} />
          <div id="sectionContainer">
            <p>Inner content</p>
          </div>
        </div>
        <Footer />
      </Fragment>
    );
  }
}

export default CommandPost;
