import React, { Fragment, Component } from 'react';
import Header from './layout/Header';
import Footer from './layout/Footer';
import MainMenu from './layout/MainMenu';

class CommandPost extends Component {

  state = {
    darkMode: false,
    userLoggedIn: false
  }

  switchThemeHandler = () => {
    const doesDarkMode = this.state.darkMode;
    this.setState({ darkMode: !doesDarkMode });
    // ReactDOM.render(<link rel="stylesheet" href={{CommandpostDark}} type="text/css"></link>, document.head);
  }

  componentDidMount() {
    const head = document.head;
    const link = document.createElement('link');

    link.type = 'text/css';
    link.rel = 'stylesheet';
    link.href = 'assets/css/commandpost.dark.css';

    head.appendChild(link);
  }

  render() {
    return (
      <Fragment>
        <Header />
        <div className="main-wrapper">
          <MainMenu
            click={() => this.switchThemeHandler()} />
          <div id="sectionContainer">
            <p>Inner content</p>
          </div>
        </div>
        <Footer />
      </Fragment>
    );
  }
}

export default CommandPost;
